import React, {useState} from 'react';
import {connect} from 'react-redux';
import {addBasket} from '../actions/addAction.jsx';
import Card from './Card.jsx';
import products from './items.js';

function createCard(item){
    return(

        <Card 
            id = {item.item_no}
            name = {item.name}
            price = {item.price}
            numbers = {item.numbers}
            incart = {item.inCart}
            img = {item.image}
            food_category = {item.food_category} 
        />  
);
}

let pushedProducts = []
function makeList(){
    Object.keys(products).forEach(function(item){
        pushedProducts.push(products[item])
    })
}

let italianList = []
function makeItalian(){
        Object.keys(products).forEach(function(item){
            if(products[item].food_category === 'italian'){
                italianList.push(products[item])
            }
        })
}

const Menu = () => {

    return (
        <div class = "container-menu">
         <div class = "menuuu">MENU</div>
         <div class="col-md-12 text-center">
            <div class="btn-group btn-group-lg">
            <a href="#" class="btn btn-primary">All</a>
            <a href="#" class="btn btn-primary">Desi</a>
            <a href="#" class="btn btn-primary">Chinese</a>
            <a href="#" class="btn btn-primary" onClick = {() => {
                {makeItalian()}
                {italianList.map(createCard)}
            }}>Italian</a>
            <a href="#" class="btn btn-primary">Sandwiches</a>
            <a href="#" class="btn btn-primary">Burgers</a>
            <a href="#" class="btn btn-primary">Desserts</a>
            </div>
        </div>
            <div class = "cards">
                {/* {makeItalian()}
                {italianList.map(createCard)} */}

            </div>

            <div>
                <ul class="pagination pagination-lg justify-content-center">
                    <li class="page-item"><a class="page-link" href="#">Previous</a></li>
                    <li class="page-item"><a class="page-link" href="#">1</a></li>
                    <li class="page-item"><a class="page-link" href="#">2</a></li>
                    <li class="page-item"><a class="page-link" href="#">3</a></li>
                    <li class="page-item"><a class="page-link" href="#">4</a></li>
                    <li class="page-item"><a class="page-link" href="#">5</a></li>
                    <li class="page-item"><a class="page-link" href="#">6</a></li>
                    <li class="page-item"><a class="page-link" href="#">7</a></li>
                    <li class="page-item"><a class="page-link" href="#">8</a></li>
                    <li class="page-item"><a class="page-link" href="#">9</a></li>
                    <li class="page-item"><a class="page-link" href="#">Next</a></li>
                </ul>
            </div>
        </div>
    );
}

export default connect(null,{addBasket})(Menu);
