import React, { Component, useState, useEffect } from "react";
import { connect } from "react-redux";
import Card from "./Card.jsx";
import { Container } from "react-bootstrap";
import firebase_integration from "../fire";
import FilterResults from "react-filter-search";
import { FetchItems } from "./actions/cart-actions";
import {Tabs, Tab} from 'react-bootstrap-tabs';

// import img1 from '../images/4.jpg'
// import img2 from '../images/8.jpg'
// import img3 from '../images/9.jpg'
// import img4 from '../images/5.png'
// import img5 from '../images/1.jpg'
// import img6 from '../images/7.jpg'


function Menu(props) {
  const [data, setData] = useState([]);

  function handleChange(event) {
    const { value } = event.target;
    setValue(value);
  }

  const [value, setValue] = useState("");

  var menudata = [];
  useEffect(() => {
    firebase_integration.database
      .collection("Menu")
      .get()
      .then((docs) => {
        
        docs.forEach((doc) => {
          menudata.push(doc.data());
        });
        // setData(menudata)
        // console.log(menudata)
        // let lol = data
        props.FetchItems(menudata);
      });
  }, menudata);

  let filtered = [];

  function makeAll(){
    filtered.splice(0,filtered.length)
    Object.keys(menudata).forEach(function(item){
        filtered.push(menudata[item])
    })
}

function makeDesi(){
  // console.log("hi")
  // console.log(menudata)
  let filtered = props.items
    // filtered.splice(0,filtered.length)

    let Promise = new promise (()=>{
      filtered.map((item)=>{
        if(item.Category === "Desi"){
          // console.log(item)
          filtered.push(item)
        }
      })

    })

    promise.then(console.log(filtered))
    
    
 
        // Object.keys(menudata).forEach(function(item){
        //   console.log(item)
            // if(menudata[item].Category === 'Desi'){
            //     filtered.push(menudata[item])
            // }
        // })
        
}

function makeChinese(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Chinese'){
                filtered.push(menudata[item])
            }
        })
}

function makeItalian(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Italian'){
                filtered.push(menudata[item])
            }
        })
}

function makeSandwich(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Sandwich'){
                filtered.push(menudata[item])
            }
        })
}

function makeBurgers(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Burgers'){
                filtered.push(menudata[item])
            }
        })
}

function makeDesserts(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Desserts'){
                filtered.push(menudata[item])
            }
        })
    }

  return (

    <div class="menuback">
      <div className="container-fluid">
        {/* {console.log(props.items)} */}
        <div>
        <div className = "tabs">

        <Tabs onSelect={(index, label) => console.log(label + ' selected')}>
    <Tab label="Tab1">{makeDesi()}</Tab>
    <Tab label="Tab2">Tab 2 content</Tab>
</Tabs>

        </div>
          <div class=" search active-pink-3 active-pink-4 mb-4">
            <input
              value={value}
              onChange={handleChange}
              class="form-control"
              type="text"
              placeholder="Search"
              aria-label="Search"
            />
          </div>

          <FilterResults
            value={value}
            data={props.items}
            renderResults={(results) => (
              <div class="container-fluid ml-2 mr-2">
                <div class="row mg">
                  {results.map((item) => {
                    return (
                      <div class=" text-center col-md-3 ml-5 mr-5">
                        <div>
                          <Card
                            id={item.DishID}
                            title={item.Name}
                            desc={item.PrepTime}
                            price={item.SalePrice}
                            img={item.URL}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          />
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    items: state.items,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    FetchItems: (items) => {
      dispatch(FetchItems(items));
    },
  };
};
// export default connect(mapStateToProps,mapDispatchToProps)(DataRead)
export default connect(mapStateToProps, mapDispatchToProps)(Menu);
