import React, { Component, useState, useEffect } from "react";
import { connect } from "react-redux";
import Card from "./Card.jsx";
import { Container } from "react-bootstrap";
import firebase_integration from "../fire";
import FilterResults from "react-filter-search";
import { FetchItems } from "./actions/cart-actions";

function Menu(props) {
  const [data, setData] = useState([]);

  function handleChange(event) {
    const { value } = event.target;
    setValue(value);
  }

  let lol = [{DishID:0,Name:'Winter body', PrepTime: "30mins", SalePrice:110,URL:img1},
    {DishID:1,Name:'AdDishIDas', PrepTime: "30mins", SalePrice:80,URL: img2},
    {DishID:2,Name:'Vans', PrepTime: "30mins",SalePrice:120,URL: img3},
    {DishID:3,Name:'White', PrepTime: "30mins", SalePrice:260,URL:img4},
    {DishID:4,Name:'Cropped-sho', PrepTime: "30mins", SalePrice:160,URL: img5},
    {DishID:5,Name:'Blues', PrepTime: "30mins",SalePrice:90,URL: img6}]

  const [value, setValue] = useState("");

  useEffect(() => {
    firebase_integration.database
      .collection("Menu")
      .get()
      .then((docs) => {
        var menu_items = [];
        docs.forEach((doc) => {
          menu_items.push(doc.data());
        });
        // setData(menu_items)
        // console.log(menu_items)
        props.FetchItems(lol)
      });
  }, data);

  console.log(props.items);

  return (
    <div class="menuback">
      <div className="container-fluid">
        {/* {console.log(props.items)} */}
        <div>
          <div class=" search active-pink-3 active-pink-4 mb-4">
            <input
              value={value}
              onChange={handleChange}
              class="form-control"
              type="text"
              placeholder="Search"
              aria-label="Search"
            />
          </div>
          {/* <input class = "search" type="text" value={value} onChange={handleChange} /> */}
          <FilterResults
            value={value}
            data={props.items}
            renderResults={(results) => (
              <div class="container-fluid ml-2 mr-2">
                <div class="row mg">
                  {results.map((item) => {
                    return (
                      <div class=" text-center col-md-3 ml-5 mr-5">
                        <div>
                          <Card
                            id={item.DishID}
                            title={item.Name}
                            desc={item.PrepTime}
                            price={item.SalePrice}
                            img={item.URL}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          />
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    items: state.items,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    FetchItems: (items) => {
      dispatch(FetchItems(items));
    },
  };
};
// export default connect(mapStateToProps,mapDispatchToProps)(DataRead)
export default connect(mapStateToProps, mapDispatchToProps)(Menu);
