import React, {useState} from "react";
import DiscountContext from '../context/discount';
// import './deals.css';

// import './index.css';

export default function Wheel(){
  const [selecteditem, setState] = useState(null);
 
  function selectItem() {
    if (selectedItem === null) {
      const selectedItem = Math.floor(Math.random() * props.items.length);
      
      if (props.onSelectItem) {
        props.onSelectItem(selectedItem);
      }
      console.log(props.items[selectedItem])
      setState({ selectedItem });
      
    } else {
      setState({ selectedItem: null });
      setTimeout(selectItem, 500);
    }
  }

 
    
    // const { selectedItem } = this.state;
    const { items } = props;

    const wheelVars = {
      "--nb-item": items.length,
      "--selected-item": selectedItem,
    };
    const spinning = selectedItem !== null ? "spinning" : "";

    return (
      <div className="wheel-container">
        <div
          className={`wheel ${spinning}`}
          style={wheelVars}
          onClick={selectItem}
        >
          {items.map((item, index) => (
            <div
              className="wheel-item"
              key={index}
              style={{ "--item-nb": index }}
            >
              {item}
            </div>
          ))}
        </div>
      </div>
    );
  }
