import React, {useState} from 'react';
import {connect} from 'react-redux';
import {addBasket} from '../actions/addAction';
import Card from './Card.jsx';
import products from './items.js';
import '../cartstyles.css';

function createCard(item){
    return(

        <Card 
            id = {item.item_no}
            name = {item.name}
            price = {item.price}
            numbers = {item.numbers}
            incart = {item.inCart}
            img = {item.image}
            food_category = {item.food_category} 
        />  
);
}
let filtered = []
function makeAll(){
    Object.keys(products).forEach(function(item){
        filtered.push(products[item])
    })
}



let desiList = []
function makeDesi(){
        Object.keys(products).forEach(function(item){
            if(products[item].food_category === 'desi'){
                filtered.push(products[item])
            }
        })
}

let chineseList = []
function makeChinese(){
        Object.keys(products).forEach(function(item){
            if(products[item].food_category === 'chinese'){
                filtered.push(products[item])
            }
        })
}

let italianList = []
function makeItalian(){
        Object.keys(products).forEach(function(item){
            if(products[item].food_category === 'italian'){
                filtered.push(products[item])
            }
        })
}

let sandwichList = []
function makeSandwich(){
        Object.keys(products).forEach(function(item){
            if(products[item].food_category === 'sandwich'){
                filtered.push(products[item])
            }
        })
}

let burgersList = []
function makeBurgers(){
        Object.keys(products).forEach(function(item){
            if(products[item].food_category === 'burgers'){
                filtered.push(products[item])
            }
        })
}

let dessertsList = []
function makeDesserts(){
        Object.keys(products).forEach(function(item){
            if(products[item].food_category === 'desserts'){
                filtered.push(products[item])
            }
        })
    }

const Menu = (props) => {


    return (
        <div className = "container-menu">
         <div className = "menuuu">MENU</div>
         <div className="col-md-12 text-center">
            <div className="btn-group btn-group-lg">
            <a href="#" className="btn btn-primary" onClick = {
                makeAll
            }>All</a>
            <a href="#" className="btn btn-primary" onClick = {
                makeDesi
            }>Desi</a>
            <a href="#" className="btn btn-primary" onClick = {
                makeChinese
            }>Chinese</a>
            <a href="#" className="btn btn-primary"onClick = {
                makeItalian
            }>Italian</a>
            <a href="#" className="btn btn-primary"onClick = {
                makeSandwich
            }>Sandwiches</a>
            <a href="#" className="btn btn-primary"onClick = {
                makeBurgers
            }>Burgers</a>
            <a href="#" className="btn btn-primary" onClick = {
                makeDesserts
            }>Desserts</a>

            </div>
        </div>
            <div className = "cards">

                {/* {makeDesserts()} */}
                {() => {filtered.map(createCard)}}
            </div>

            <div>
                <ul className="pagination pagination-lg justify-content-center">
                    <li className="page-item"><a className="page-link" href="#">Prev</a></li>
                    <li className="page-item"><a className="page-link" href="#">1</a></li>
                    <li className="page-item"><a className="page-link" href="#">2</a></li>
                    <li className="page-item"><a className="page-link" href="#">3</a></li>
                    <li className="page-item"><a className="page-link" href="#">4</a></li>
                    <li className="page-item"><a className="page-link" href="#">5</a></li>
                    <li className="page-item"><a className="page-link" href="#">6</a></li>
                    <li className="page-item"><a className="page-link" href="#">7</a></li>
                    <li className="page-item"><a className="page-link" href="#">8</a></li>
                    <li className="page-item"><a className="page-link" href="#">9</a></li>
                    <li className="page-item"><a className="page-link" href="#">Next</a></li>
                </ul>
            </div>
        </div>
    );
}

export default connect(null,{addBasket})(Menu)
