      if(item.Category === "Chinese"){
        // console.log(item)
        Chinese.push(item)
      }
    })

  
 
}


let Italian = []
function makeItalian(){
  // filtered.splice(0,filtered.length)
    props.items.map((item)=>{
      if(item.Category === "Italian"){
        // console.log(item)
        Italian.push(item)
      }
    })

 
}


let Sandwich = []
function makeSandwich(){
  // filtered.splice(0,filtered.length)
    props.items.map((item)=>{
      if(item.Category === "Sandwich"){
        // console.log(item)
        Sandwich.push(item)
      }
    })

 
}


let Burger = []
function makeBurgers(){
  // filtered.splice(0,filtered.length)
    props.items.map((item)=>{
      if(item.Category === "Burgers"){
        // console.log(item)
        Burger.push(item)
      }
    })

  
 
}


let Dessert = []
function makeDesserts(){
  // filtered.splice(0,filtered.length)
    props.items.map((item)=>{
      if(item.Category === "Desserts"){
        // console.log(item)
        Dessert.push(item)
      }
    })

 
}




function renderData(data){
  makeAll();
  makeBurgers();
  makeDesi();
  makeDesserts();
  makeItalian();
  makeSandwich();
  makeChinese();
  return(
    <div>
    <div class=" search active-pink-3 active-pink-4 mb-4">
            <input
              value={value}
              onChange={handleChange}
              class="form-control"
              type="text"
              placeholder="Search"
              aria-label="Search"
            />
          </div>

          {console.log(data)}

          <FilterResults
            value={value}
            data={data}
            renderResults={(results) => (
              <div class="container-fluid ml-2 mr-2">
                <div class="row mg">
                  {results.map((item) => {
                    return (
                      <div class=" text-center col-md-3 ml-5 mr-5">
                        <div>
                          <Card
                            id={item.DishID}
                            title={item.Name}
                            desc={item.PrepTime}
                            price={item.SalePrice}
                            img={item.URL}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          />
          </div>
  );
}




  return (

    <div class="menuback">
      <div className="container-fluid">
        {/* {console.log(props.items)} */}
        <div>
        <div className = "tabs">

        <Tabs onSelect={(index, label) => console.log(label + ' selected')}>
    <Tab label="All"><div>
    <div class=" search active-pink-3 active-pink-4 mb-4">
            <input
              value={value}
              onChange={handleChange}
              class="form-control"
              type="text"
              placeholder="Search"
              aria-label="Search"
            />
          </div>

          {console.log(data)}

          <FilterResults
            value={value}
            data={props.items}
            renderResults={(results) => (
              <div class="container-fluid ml-2 mr-2">
                <div class="row mg">
                  {results.map((item) => {
                    return (
                      <div class=" text-center col-md-3 ml-5 mr-5">
                        <div>
                          <Card
                            id={item.DishID}
                            title={item.Name}
                            desc={item.PrepTime}
                            price={item.SalePrice}
                            img={item.URL}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          />
          </div></Tab>
    <Tab label="Desi">{renderData(filtered)}</Tab>
    <Tab label="Desserts">{makeDesserts()}</Tab>
    <Tab label="Italian">{makeItalian()}</Tab>
    <Tab label="Burgers">{makeBurgers()}</Tab>
    <Tab label="Chinese">{makeChinese()}</Tab>
    <Tab label="Sandwiches">{makeSandwich()}</Tab>
</Tabs>

</div>
          
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    items: state.items,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    FetchItems: (items) => {
      dispatch(FetchItems(items));
    },
  };
};
