import React from "react";
import DiscountContext from '../context/context';
// import './deals.css';

// import './index.css';

export default class Wheel extends React.Component {
  static contextType = DiscountContext;
 
  constructor(props,context) {
    super(props,context);
    this.state = {
      selectedItem: null,
    };
    this.selectItem = this.selectItem.bind(this);
  }



  render() {
    const { setDiscount } = this.context;
    // console.log(this.context)
    const { selectedItem } = this.state;
    const { items } = this.props;

    const givediscount = () => {
      console.log("ho")
       setDiscount(this.props.items[this.state.selectedItem]);
       console.log(this.context)
    }


    const selectItem=()=> {
      if (this.state.selectedItem === null) {
        const selectedItem = Math.floor(Math.random() * this.props.items.length);
        
        if (this.props.onSelectItem) {
          this.props.onSelectItem(selectedItem);
        }
        console.log(this.props.items[selectedItem])
        givediscount();
        this.setState({ selectedItem });
        
      } else {
        this.setState({ selectedItem: null });
        setTimeout(this.selectItem, 500);
      }
    }


    const wheelVars = {
      "--nb-item": items.length,
      "--selected-item": selectedItem,
    };
    const spinning = selectedItem !== null ? "spinning" : "";

    return (
      <div className="wheel-container">
        <div
          className={`wheel ${spinning}`}
          style={wheelVars}
          onClick={selectItem}
        >
          {items.map((item, index) => (
            <div
              className="wheel-item"
              key={index}
              style={{ "--item-nb": index }}
            >
              {item}
              
            </div>
          ))}
          
          {/* {console.log(this.context)} */}
         
        </div>
      </div>
    );
  
  }
}
