import React from "react";
import DiscountContext from "../context/context";
import './deals.css';

// import './index.css';

export default function Wheel(props) {
  // static contextType = DiscountContext;
  const lol = useContext(DiscountContext)

  const [selectedItem, setState] = useState(null)
  let value = 0



  const selectItem = () => {
    if (selectedItem === null) {
 value = Math.floor(Math.random() * props.items.length);
      console.log(value)

      // if (this.props.onSelectItem) {
      //   this.props.onSelectItem(selectedItem);
      // }
      // console.log(this.props.items[selectedItem]);
      setState({ selectedItem: value });
      // console.log(this.state.selectedItem)
      // givediscount();
    } else {
      // this.setState({ selectedItem: null });
      setTimeout(500);
      // this.value = Math.floor(Math.random() * this.props.items.length);
      // console.log(this.context)
      // this.context.setDiscount(100);


      // // if (this.props.onSelectItem) {
      // //   this.props.onSelectItem(selectedItem);
      // // }
      // // console.log(this.props.items[selectedItem]);
      // this.setState({ selectedItem: this.value });
    }
  }
    const { setDiscount } = lol;
    // console.log(this.context)
    // const { selectedItem } = state;
    const { items } = props;

    const givediscount = (val) => {
      console.log(val);
      setDiscount(val);
      console.log(lol);
    };

    const wheelVars = {
      "--nb-item": items.length,
      "--selected-item": selectedItem,
    };
    const spinning = selectedItem !== null ? "spinning" : "";

    return (
      <div className="wheel-container">
        <div
          className={`wheel ${spinning}`}
          style={wheelVars}
          onClick={() => {
            let promise = new Promise (() => {selectItem()})
            setTimeout(1000)
            promise.then(givediscount(props.items[value]))
          }}
        >
          {items.map((item, index) => (
            <div
              className="wheel-item"
              key={index}
              style={{ "--item-nb": index }}
            >
              {item}
            </div>
          ))}
          {/* {givediscount()} */}

          {/* {console.log(this.context)} */}
        </div>
      </div>
    );
  
}
