import React from "react";
// import Button from "./loginbutton";
import LoginFront from "./loginfront";
import ReactCardFlip from "react-card-flip";
import SignupBack from "./signupback";
import SignupFront from "./signupfront";

function LoginBack() {
  // constructor() {
  //   super();
  //   this.state = {
  //     isFlipped: false,
  //   };
  //   this.handleClick = this.handleClick.bind(this);
  // }

  // var isFlipped = false;
  const [isFlipped, setState] = useState(false);

  function handleClick(e) {
    e.preventDefault();
    setState((prevState) => ({ isFlipped: !prevState.isFlipped }));
  }


    return (
      <div class ="cont row">
        <div class = "col-lg- leftcard">
        <ReactCardFlip
          isFlipped={this.state.isFlipped}
          flipDirection="horizontal"
        >
            <div class="logcardback1">
              <div class="button-position">
                <label class="login-button">
                  <input type="checkbox" />
                  <span class="back">
                    <span class="toggle"></span>
                    <span class="label on">LOGIN</span>
                    <span onClick={this.handleClick} class="label off">
                      SIGN UP
                    </span>
                  </span>
                </label>
              </div>
              <div className="login-text">WELCOME BACK!</div>
              <div className="form-div">
                <div class="modal-dialog modal-login">
                  <div class="modal-content">
                    <div class="modal-header">
                      <h4 class="modal-title">LOGIN WITH EMAIL</h4>
                    </div>
                    <div class="modal-body">
                      <form
                        action="/examples/actions/confirmation.php"
                        method="post"
                      >
                        <div class="form-group">
                          <input
                            type="text"
                            class="form-control"
                            placeholder="Username"
                            required="required"
                          />
                        </div>
                        <div class="form-group">
                          <input
                            type="password"
                            class="form-control"
                            placeholder="Password"
                            required="required"
                          />
                        </div>
                        <div class="form-group">
                          <input
                            type="submit"
                            class="btn btn-primary btn-block btn-lg"
                            value="Sign in"
                          />
                        </div>
                      </form>
                      <p class="hint-text small">
                        <a href="#">Forgot Your Password?</a>
                      </p>
                      <div class="hint-text small">Don't have an account? <a type = "button" onClick = {this.handleClick} href="#" class="text-col">Sign Up!</a></div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          <div>
            <LoginFront />
          </div>
        </ReactCardFlip>
        </div>
        <div class = "col-lg- leftcard">
          <div>
        <ReactCardFlip
          isFlipped={this.state.isFlipped}
          flipDirection="horizontal"
        >
          <div>
            <SignupFront />
          </div>
          <div>
            <SignupBack />
          </div>
        </ReactCardFlip>
      
        </div>
        </div>
        </div>
    );
  
}

export default LoginBack;
