import React, { Component, useState, useEffect } from "react";
import { connect } from "react-redux";
import Card from "./Card.jsx";
import { Container } from "react-bootstrap";
import firebase_integration from "../fire";
import FilterResults from "react-filter-search";
import { FetchItems } from "./actions/cart-actions";

var temp = [];
async function fetchMenu() {
  var docs = await firebase_integration.database.collection("Menu").get();
  var menu_items = [];
  docs.forEach((doc) => {
    menu_items.push(doc.data());
  });
  temp = menu_items;
}

async function menuRead() {
  await fetchMenu();
}

// menuRead();



// export const DataRead = (props) => {

// }

function Menu(props){
  

  // async function handleData(items) {
  //   await menuRead();
  //   props.FetchItems(items);
  //   // console.log(items);
  // }

  // handleData(temp);
  




  let data = props.items;
  const [fooditems, setFooditems] = useState([]);
  const [value, setValue] = useState("");
  function handleChange(event) {
    const { value } = event.target;
    setValue(value);
  }

  return (
    <div class="menuback">
      <div className="container-fluid">
        {console.log(props.items)}
        <div>
          <div class=" search active-pink-3 active-pink-4 mb-4">
            <input
              value={value}
              onChange={handleChange}
              class="form-control"
              type="text"
              placeholder="Search"
              aria-label="Search"
            />
          </div>
          {/* <input class = "search" type="text" value={value} onChange={handleChange} /> */}
          <FilterResults
            value={value}
            data={data}
            renderResults={(results) => (
              <div class="container-fluid ml-2 mr-2">
                <div class="row mg">
                  {results.map((item) => {
                    return (
                      <div class=" text-center col-md-3 ml-5 mr-5">
                        <div>
                          <Card
                            id={item.DishID}
                            title={item.Name}
                            desc={item.PrepTime}
                            price={item.SalePrice}
                            img={item.URL}
                            items={data}
                          />
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          />
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    items: state.items,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    FetchItems: (items) => {
      dispatch(FetchItems(items));
    },
  };
};
// export default connect(mapStateToProps,mapDispatchToProps)(DataRead)
export default connect(mapStateToProps, mapDispatchToProps)(Menu);
