import React from "react";
// import Button from "./loginbutton";
import LoginFront from "./loginfront";
import ReactCardFlip from "react-card-flip";
import SignupBack from "./signupback";
import SignupFront from "./signupfront";
import { useState, useEffect } from "react";
import Firebase from "./fire";
import { Link, withRouter } from "react-router-dom";
import Login from "./login";
import { FacebookLoginButton } from "react-social-login-buttons";

function LoginBack(props) {
  const [isFlipped, setState] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function handleClick(e) {
    e.preventDefault();
    setState((prevState) => ({ isFlipped: !prevState.isFlipped }));
  }

  return (
    <div class="cont row">
      <div class="col-lg- leftcard">
        <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
          <div class="logcardback1">
            <div class="button-position">
              <label class="login-button">
                <input type="checkbox" />
                <span class="back">
                  <span class="toggle"></span>
                  <span class="label on">LOGIN</span>
                  <span onClick={handleClick} class="label off">
                    SIGN UP
                  </span>
                </span>
              </label>
            </div>
            <div className="login-text">WELCOME BACK!</div>
            <div className="form-div">
              <div class="modal-dialog modal-login">
                <div class="modal-content">
                  <div class="modal-header">
                    <h4 class="modal-title">LOGIN WITH EMAIL</h4>
                  </div>
                  <div class="modal-body">
                    <form
                      action="/examples/actions/confirmation.php"
                      method="post"
                      onSubmit={(e) => e.preventDefault() && false}
                    >
                      <div class="form-group">
                        <input
                          type="text"
                          class="form-control"
                          placeholder="Email"
                          required="required"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                        />
                      </div>
                      <div class="form-group">
                        <input
                          type="password"
                          class="form-control"
                          placeholder="Password"
                          required="required"
                          value={password}
                          onChange={(p) => setPassword(p.target.value)}
                        />
                      </div>
                      <div class="form-group">
                        <input
                          type="submit"
                          class="btn btn-primary btn-block btn-lg"
                          value="Sign in"
                          onClick = {login}
                        />
                      </div>
                    </form>
                    <p class="hint-text small">
                      <a onClick = {Firebase.passwordreset}
                        href="#">Forgot Your Password?</a>
                    </p>
                    <div class="hint-text small">
                      Don't have an account?{" "}
                      <a
                        type="button"
                        onClick={handleClick}
                        href="#"
                        class="text-col"
                      >
                        Sign Up!
                      </a>
                      <FacebookLoginButton onClick={() => Firebase.doSignInWithFacebook()
      .then(socialAuthUser => {
        this.setState({ error: null });
        this.props.history.push(ROUTES.HOME);
      })
      .catch(error => {
        this.setState({ error });
      })} />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div>
            <LoginFront />
          </div>
        </ReactCardFlip>
      </div>
      <div class="col-lg- rightcard">
        <div>
          <ReactCardFlip isFlipped={isFlipped} flipDirection="horizontal">
            <div>
              <SignupFront />
            </div>
            <div>
              <SignupBack />
            </div>
          </ReactCardFlip>
        </div>
      </div>
    </div>
  );

  async function login() {
    try {
      await Firebase.login(email, password);
      alert("logged in");
      props.history.replace("/");
    } catch (error) {
      alert(error.message);
    }
  }
}

export default withRouter(LoginBack);
