import React, { Component, useState, useEffect } from "react";
import { connect } from "react-redux";
import Card from "./Card.jsx";
import { Container } from "react-bootstrap";
import firebase_integration from "../fire";
import FilterResults from "react-filter-search";
import { FetchItems } from "./actions/cart-actions";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
// import img1 from '../images/4.jpg'
// import img2 from '../images/8.jpg'
// import img3 from '../images/9.jpg'
// import img4 from '../images/5.png'
// import img5 from '../images/1.jpg'
// import img6 from '../images/7.jpg'


function Menu(props) {
  const [data, setData] = useState([]);

  function handleChange(event) {
    const { value } = event.target;
    setValue(value);
  }

  const [value, setValue] = useState("");

  var menudata = [];
  useEffect(() => {
    firebase_integration.database
      .collection("Menu")
      .get()
      .then((docs) => {
        
        docs.forEach((doc) => {
          menudata.push(doc.data());
        });
        // setData(menudata)
        // console.log(menudata)
        // let lol = data
        props.FetchItems(menudata);
      });
  }, menudata);

  let filtered = [];

  function makeAll(){
    filtered.splice(0,filtered.length)
    Object.keys(menudata).forEach(function(item){
        filtered.push(menudata[item])
    })
}

function makeDesi(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Desi'){
                filtered.push(menudata[item])
            }
        })
}

function makeChinese(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Chinese'){
                filtered.push(menudata[item])
            }
        })
}

function makeItalian(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Italian'){
                filtered.push(menudata[item])
            }
        })
}

function makeSandwich(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Sandwich'){
                filtered.push(menudata[item])
            }
        })
}

function makeBurgers(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Burgers'){
                filtered.push(menudata[item])
            }
        })
}

function makeDesserts(){
    filtered.splice(0,filtered.length)
        Object.keys(menudata).forEach(function(item){
            if(menudata[item].Category === 'Desserts'){
                filtered.push(menudata[item])
            }
        })
    }

  return (
    <div class="menuback">
      <div className="container-fluid">
        {/* {console.log(props.items)} */}
        <div>
        <div className = "tabs">
          {/* <Tabs forceRenderTabPanel defaultIndex={0}>
            <TabList>
              <Tab>All</Tab>
              <Tab>Desi</Tab>
              <Tab>Chinese</Tab>
              <Tab>Italian</Tab>
              <Tab>Sandwich</Tab>
              <Tab>Burgers</Tab>
              <Tab>Desserts</Tab>
            </TabList>
            <TabPanel>
              {makeAll}
            </TabPanel>
            <TabPanel>
              {makeDesi}
            </TabPanel>
            <TabPanel>
              {makeChinese}
            </TabPanel>
            <TabPanel>
              {makeItalian}
            </TabPanel>
            <TabPanel>
              {makeSandwich}
            </TabPanel>
            <TabPanel>
              {makeBurgers}
            </TabPanel>
            <TabPanel>
              {makeDesserts}
            </TabPanel>
          </Tabs> */}

<Tabs forceRenderTabPanel defaultIndex={1}>
    <TabList>
      <Tab>The Simpsons</Tab>
      <Tab>Futurama</Tab>
    </TabList>
    <TabPanel>
      <Tabs forceRenderTabPanel>
        <TabList>
          <Tab>Homer Simpson</Tab>
          <Tab>Marge Simpson</Tab>
          <Tab>Bart Simpson</Tab>
          <Tab>Lisa Simpson</Tab>
          <Tab>Maggie Simpson</Tab>
